import React, { useEffect, useRef } from 'react';
import lottie from 'lottie-web';

function AnimatedSticker() {
  const containerRef = useRef(null);

  useEffect(() => {
    let animation = null;

    if (containerRef.current) {
      // Используем простую анимацию
      const animationData = {
        v: "5.7.4",
        fr: 60,
        ip: 0,
        op: 180,
        w: 200,
        h: 200,
        nm: "Simple Animation",
        ddd: 0,
        assets: [],
        layers: [
          {
            ddd: 0,
            ind: 1,
            ty: 4,
            nm: "Circle",
            sr: 1,
            ks: {
              o: { a: 0, k: 100 },
              r: { 
                a: 1, 
                k: [
                  { t: 0, s: [0], h: 1 },
                  { t: 90, s: [360], h: 1 }
                ] 
              },
              p: { a: 0, k: [100, 100] },
              s: { a: 0, k: [100, 100] }
            },
            shapes: [
              {
                ty: "el",
                p: { a: 0, k: [0, 0] },
                s: { a: 0, k: [80, 80] },
                d: 1
              },
              {
                ty: "fl",
                c: { a: 0, k: [0.2, 0.4, 1, 1] },
                o: { a: 0, k: 100 }
              }
            ]
          }
        ]
      };

      animation = lottie.loadAnimation({
        container: containerRef.current,
        renderer: 'svg',
        loop: true,
        autoplay: true,
        animationData
      });
    }

    return () => {
      if (animation) {
        animation.destroy();
      }
    };
  }, []);

  return (
    <div 
      ref={containerRef} 
      style={{ 
        width: '200px', 
        height: '200px',
        margin: '0 auto'
      }} 
    />
  );
}

export default AnimatedSticker;
